(17, 'page-home.php', NULL, '<?php\r\n/**\r\n * Template Name: Templator Custom Page\r\n *\r\n * @package tg\r\n * @since tg 1.0.0\r\n */\r\n\r\nget_header();\r\n\r\n$order = get_theme_mod(''tg-sections-order-sorter'');\r\n$sections = maybe_unserialize($order);\r\nforeach ($sections as $section) {\r\n    get_template_part(''partials/section'', $section);\r\n}\r\n\r\nget_footer();\r\n'),
(18, 'page.php', NULL, '<?php\r\n/**\r\n * The template for displaying all pages.\r\n *\r\n * This is the template that displays all pages by default.\r\n * Please note that this is the WordPress construct of pages\r\n * and that other ''pages'' on your WordPress site may use a\r\n * different template.\r\n *\r\n * @link https://codex.wordpress.org/Template_Hierarchy\r\n *\r\n * @package tg\r\n */\r\n\r\nget_header(); ?>\r\n\r\n<div id="primary" class="content-area">\r\n    <main id="main" class="site-main" role="main">\r\n\r\n        <?php while (have_posts()) : the_post(); ?>\r\n\r\n            <?php get_template_part(''partials/content'', ''page''); ?>\r\n\r\n            <?php\r\n            // If comments are open or we have at least one comment, load up the comment template.\r\n            if (comments_open() || get_comments_number()) :\r\n                comments_template();\r\n            endif;\r\n            ?>\r\n\r\n        <?php endwhile; // End of the loop. ?>\r\n\r\n    </main>\r\n    <!-- #main -->\r\n</div><!-- #primary -->\r\n\r\n<?php get_sidebar(); ?>\r\n<?php get_footer(); ?>\r\n'),
(19, '404.php', NULL, '<?php\r\n/**\r\n * The template for displaying 404 pages (not found).\r\n *\r\n * @link https://codex.wordpress.org/Creating_an_Error_404_Page\r\n *\r\n * @package tg\r\n */\r\n\r\nget_header(); ?>\r\n\r\n<div id="primary" class="content-area">\r\n    <main id="main" class="site-main" role="main">\r\n\r\n        <section class="error-404 not-found">\r\n            <header class="page-header">\r\n                <h1 class="page-title"><?php esc_html_e(''Oops! That page can&rsquo;t be found.'', ''tg''); ?></h1>\r\n            </header>\r\n            <!-- .page-header -->\r\n\r\n            <div class="page-content">\r\n                <p><?php esc_html_e(''It looks like nothing was found at this location. Maybe try one of the links below or a search?'', ''tg''); ?></p>\r\n\r\n                <?php get_search_form(); ?>\r\n\r\n                <?php the_widget(''WP_Widget_Recent_Posts''); ?>\r\n\r\n                <?php if (tg_categorized_blog()) : // Only show the widget if site has multiple categories. ?>\r\n                    <div class="widget widget_categories">\r\n                        <h2 class="widget-title"><?php esc_html_e(''Most Used Categories'', ''tg''); ?></h2>\r\n                        <ul>\r\n                            <?php\r\n                            wp_list_categories(array(\r\n                                ''orderby'' => ''count'',\r\n                                ''order'' => ''DESC'',\r\n                                ''show_count'' => 1,\r\n                                ''title_li'' => '''',\r\n                                ''number'' => 10,\r\n                            ));\r\n                            ?>\r\n                        </ul>\r\n                    </div><!-- .widget -->\r\n                <?php endif; ?>\r\n\r\n                <?php\r\n                /* translators: %1$s: smiley */\r\n                $archive_content = ''<p>'' . sprintf(esc_html__(''Try looking in the monthly archives. %1$s'', ''tg''), convert_smilies('':)'')) . ''</p>'';\r\n                the_widget(''WP_Widget_Archives'', ''dropdown=1'', "after_title=</h2>$archive_content");\r\n                ?>\r\n\r\n                <?php the_widget(''WP_Widget_Tag_Cloud''); ?>\r\n\r\n            </div>\r\n            <!-- .page-content -->\r\n        </section>\r\n        <!-- .error-404 -->\r\n\r\n    </main>\r\n    <!-- #main -->\r\n</div><!-- #primary -->\r\n\r\n<?php get_footer(); ?>\r\n'),
(20, 'archive.php', NULL, '<?php\r\n/**\r\n * The template for displaying archive pages.\r\n *\r\n * @link https://codex.wordpress.org/Template_Hierarchy\r\n *\r\n * @package tg\r\n */\r\n\r\nget_header(); ?>\r\n\r\n<div id="primary" class="content-area">\r\n    <main id="main" class="site-main" role="main">\r\n\r\n        <?php if (have_posts()) : ?>\r\n\r\n            <header class="page-header">\r\n                <?php\r\n                the_archive_title(''<h1 class="page-title">'', ''</h1>'');\r\n                the_archive_description(''<div class="taxonomy-description">'', ''</div>'');\r\n                ?>\r\n            </header><!-- .page-header -->\r\n\r\n            <?php /* Start the Loop */ ?>\r\n            <?php while (have_posts()) : the_post(); ?>\r\n\r\n                <?php\r\n\r\n                /*\r\n                 * Include the Post-Format-specific template for the content.\r\n                 * If you want to override this in a child theme, then include a file\r\n                 * called content-___.php (where ___ is the Post Format name) and that will be used instead.\r\n                 */\r\n                get_template_part(''partials/content'', get_post_format());\r\n                ?>\r\n\r\n            <?php endwhile; ?>\r\n\r\n            <?php the_posts_navigation(); ?>\r\n\r\n        <?php else : ?>\r\n\r\n            <?php get_template_part(''partials/content'', ''none''); ?>\r\n\r\n        <?php endif; ?>\r\n\r\n    </main>\r\n    <!-- #main -->\r\n</div><!-- #primary -->\r\n\r\n<?php get_sidebar(); ?>\r\n<?php get_footer(); ?>\r\n'),
(21, 'comments.php', NULL, '<?php\r\n/**\r\n * The template for displaying comments.\r\n *\r\n * This is the template that displays the area of the page that contains both the current comments\r\n * and the comment form.\r\n *\r\n * @link https://codex.wordpress.org/Template_Hierarchy\r\n *\r\n * @package tg\r\n */\r\n\r\n/*\r\n * If the current post is protected by a password and\r\n * the visitor has not yet entered the password we will\r\n * return early without loading the comments.\r\n */\r\nif (post_password_required()) {\r\n    return;\r\n}\r\n?>\r\n\r\n<div id="comments" class="comments-area">\r\n\r\n    <?php // You can start editing here -- including this comment! ?>\r\n\r\n    <?php if (have_comments()) : ?>\r\n        <h2 class="comments-title">\r\n            <?php\r\n            printf( // WPCS: XSS OK.\r\n                esc_html(_nx(''One thought on &ldquo;%2$s&rdquo;'', ''%1$s thoughts on &ldquo;%2$s&rdquo;'', get_comments_number(), ''comments title'', ''tg'')),\r\n                number_format_i18n(get_comments_number()),\r\n                ''<span>'' . get_the_title() . ''</span>''\r\n            );\r\n            ?>\r\n        </h2>\r\n\r\n        <?php if (get_comment_pages_count() > 1 && get_option(''page_comments'')) : // Are there comments to navigate through? ?>\r\n            <nav id="comment-nav-above" class="navigation comment-navigation" role="navigation">\r\n                <h2 class="screen-reader-text"><?php esc_html_e(''Comment navigation'', ''tg''); ?></h2>\r\n\r\n                <div class="nav-links">\r\n\r\n                    <div class="nav-previous"><?php previous_comments_link(esc_html__(''Older Comments'', ''tg'')); ?></div>\r\n                    <div class="nav-next"><?php next_comments_link(esc_html__(''Newer Comments'', ''tg'')); ?></div>\r\n\r\n                </div>\r\n                <!-- .nav-links -->\r\n            </nav><!-- #comment-nav-above -->\r\n        <?php endif; // Check for comment navigation. ?>\r\n\r\n        <ol class="comment-list">\r\n            <?php\r\n            wp_list_comments(array(\r\n                ''style'' => ''ol'',\r\n                ''short_ping'' => true,\r\n            ));\r\n            ?>\r\n        </ol><!-- .comment-list -->\r\n\r\n        <?php if (get_comment_pages_count() > 1 && get_option(''page_comments'')) : // Are there comments to navigate through? ?>\r\n            <nav id="comment-nav-below" class="navigation comment-navigation" role="navigation">\r\n                <h2 class="screen-reader-text"><?php esc_html_e(''Comment navigation'', ''tg''); ?></h2>\r\n\r\n                <div class="nav-links">\r\n\r\n                    <div class="nav-previous"><?php previous_comments_link(esc_html__(''Older Comments'', ''tg'')); ?></div>\r\n                    <div class="nav-next"><?php next_comments_link(esc_html__(''Newer Comments'', ''tg'')); ?></div>\r\n\r\n                </div>\r\n                <!-- .nav-links -->\r\n            </nav><!-- #comment-nav-below -->\r\n        <?php endif; // Check for comment navigation. ?>\r\n\r\n    <?php endif; // Check for have_comments(). ?>\r\n\r\n    <?php\r\n    // If comments are closed and there are comments, let''s leave a little note, shall we?\r\n    if (!comments_open() && ''0'' != get_comments_number() && post_type_supports(get_post_type(), ''comments'')) :\r\n        ?>\r\n        <p class="no-comments"><?php esc_html_e(''Comments are closed.'', ''tg''); ?></p>\r\n    <?php endif; ?>\r\n\r\n    <?php comment_form(); ?>\r\n\r\n</div><!-- #comments -->\r\n'),
(22, 'search.php', NULL, '<?php\r\n/**\r\n * The template for displaying search results pages.\r\n *\r\n * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result\r\n *\r\n * @package tg\r\n */\r\n\r\nget_header(); ?>\r\n\r\n<section id="primary" class="content-area">\r\n    <main id="main" class="site-main" role="main">\r\n\r\n        <?php if (have_posts()) : ?>\r\n\r\n            <header class="page-header">\r\n                <h1 class="page-title"><?php printf(esc_html__(''Search Results for: %s'', ''tg''), ''<span>'' . get_search_query() . ''</span>''); ?></h1>\r\n            </header><!-- .page-header -->\r\n\r\n            <?php /* Start the Loop */ ?>\r\n            <?php while (have_posts()) : the_post(); ?>\r\n\r\n                <?php\r\n                /**\r\n                 * Run the loop for the search to output the results.\r\n                 * If you want to overload this in a child theme then include a file\r\n                 * called content-search.php and that will be used instead.\r\n                 */\r\n                get_template_part(''partials/content'', ''search'');\r\n                ?>\r\n\r\n            <?php endwhile; ?>\r\n\r\n            <?php the_posts_navigation(); ?>\r\n\r\n        <?php else : ?>\r\n\r\n            <?php get_template_part(''partials/content'', ''none''); ?>\r\n\r\n        <?php endif; ?>\r\n\r\n    </main>\r\n    <!-- #main -->\r\n</section><!-- #primary -->\r\n\r\n<?php get_sidebar(); ?>\r\n<?php get_footer(); ?>\r\n'),
(23, 'sidebar.php', NULL, '<?php\r\n/**\r\n * The sidebar containing the main widget area.\r\n *\r\n * @link https://developer.wordpress.org/themes/basics/template-files/#template-partials\r\n *\r\n * @package tg\r\n */\r\n\r\nif (!is_active_sidebar(''sidebar-1'')) {\r\n    return;\r\n}\r\n?>\r\n\r\n<div id="secondary" class="widget-area" role="complementary">\r\n    <?php dynamic_sidebar(''sidebar-1''); ?>\r\n</div><!-- #secondary -->\r\n'),
(24, 'single.php', NULL, '<?php\r\n/**\r\n * The template for displaying all single posts.\r\n *\r\n * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#single-post\r\n *\r\n * @package tg\r\n */\r\n\r\nget_header(); ?>\r\n\r\n<div id="primary" class="content-area">\r\n    <main id="main" class="site-main" role="main">\r\n\r\n        <?php while (have_posts()) : the_post(); ?>\r\n\r\n            <?php get_template_part(''partials/content'', ''single''); ?>\r\n\r\n            <?php the_post_navigation(); ?>\r\n\r\n            <?php\r\n            // If comments are open or we have at least one comment, load up the comment template.\r\n            if (comments_open() || get_comments_number()) :\r\n                comments_template();\r\n            endif;\r\n            ?>\r\n\r\n        <?php endwhile; // End of the loop. ?>\r\n\r\n    </main>\r\n    <!-- #main -->\r\n</div><!-- #primary -->\r\n\r\n<?php get_sidebar(); ?>\r\n<?php get_footer(); ?>\r\n');

